[{"operationName":"SDPPageQuery","variables":{"slug":"graphic-design"},"query":"query SDPPageQuery($slug: String!) {
  XdpV1Resource {
    slug(productType: "SPECIALIZATION", slug: $slug) {
      elements {
        id
        xdpMetadata {
          __typename
          ... on XdpV1_sdpMetadataMember {
            sdpMetadata {
              id
              avgLearningMonthsAdjusted
              avgLearningHoursPerWeek
              projectsOverview {
                ... on XdpV1_cmlMember {
                  cml {
                    dtdId
                    value
                    __typename
                  }
                  __typename
                }
                __typename
              }
              domains {
                domainId
                domainName
                subdomainName
                subdomainId
                __typename
              }
              degrees {
                degree {
                  name
                  shortName
                  slug
                  headerImage
                  __typename
                }
                __typename
              }
              name
              slug
              cmlDescription {
                __typename
                ... on XdpV1_cmlMember {
                  cml {
                    value
                    dtdId
                    __typename
                  }
                  __typename
                }
              }
              description
              skills
              primaryLanguages
              subtitleLanguages
              cmlRecommendedBackground {
                ... on XdpV1_cmlMember {
                  cml {
                    value
                    dtdId
                    __typename
                  }
                  __typename
                }
                __typename
              }
              badges
              headline
              isCertificate
              productVariant
              subheader
              ratings {
                averageFiveStarRating
                __typename
              }
              certificateDescription
              incentives
              level
              cmlLearningObjectives {
                ... on XdpV1_cmlMember {
                  cml {
                    value
                    dtdId
                    __typename
                  }
                  __typename
                }
                __typename
              }
              s12nAndCourseLearningObjectives {
                ... on XdpV1_cmlMember {
                  cml {
                    value
                    dtdId
                    __typename
                  }
                  __typename
                }
                __typename
              }
              s12nAndCourseSkills
              learningOutcomes {
                careerOutcomePromotion
                careerOutcomeNewCareer
                careerOutcomePayIncrease
                __typename
              }
              companyLogos
              partners {
                id
                name
                shortName
                landingPageBanner
                description
                partnerMarketingBlurb
                classLogo
                logo
                squareLogo
                rectangularLogo
                primaryColor
                secondaryColor
                accentColor
                productBrandingLogo
                __typename
              }
              instructors {
                id
                fullName
                photo
                title
                shortName
                department
                learnersReached
                coursesTaught
                isTopInstructor
                __typename
              }
              certificateLogo
              ratings {
                averageFiveStarRating
                commentCount
                ratingCount
                __typename
              }
              cmlFaqs {
                question {
                  ... on XdpV1_cmlMember {
                    cml {
                      dtdId
                      value
                      __typename
                    }
                    __typename
                  }
                  __typename
                }
                answer {
                  ... on XdpV1_cmlMember {
                    cml {
                      dtdId
                      value
                      __typename
                    }
                    __typename
                  }
                  __typename
                }
                __typename
              }
              courses {
                id
                name
                description
                slug
                ratings {
                  averageFiveStarRating
                  ratingCount
                  commentCount
                  __typename
                }
                averageContentSatisfactionScore
                averageInstructorRating
                material {
                  weeks {
                    modules {
                      id
                      name
                      description
                      totalVideos
                      totalQuizzes
                      totalDuration
                      totalReadings
                      totalLectureDuration
                      __typename
                    }
                    __typename
                  }
                  __typename
                }
                __typename
              }
              __typename
            }
            __typename
          }
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
"}]
