{"query":"{
    jobDetail(jobId: "100003007701559",country: "hk",locale: "en") {
      id
      pageUrl
      jobTitleSlug
      applyUrls {
       mobile
       external
       loggedInApply
      }
      isExpired
      isConfidential
      isClassified
      accountNum
      advertisementId
      subAccount
      showMoreJobs
      adType
      header {
        banner {
          bannerUrls {
            large
          }
        }
        salary {
          max
          min
          type
          extraInfo
          currency
          isVisible
          salaryOnDisplay
        }
        logoUrls {
          small
          medium
          large
          normal
        }
        jobTitle
        company {
          name
          url
        }
        review {
          rating
          numberOfReviewer
        }
        expiration
        postedDate
        isInternship
      }
      companyDetail {
        companyWebsite
        companySnapshot {
          avgProcessTime
          registrationNo
          employmentAgencyPersonnelNumber
          employmentAgencyNumber
          telephoneNumber
          workingHours
          website
          facebook
          size
          dressCode
          nearbyLocations
        }
        companyOverview {
          html
        }
        videoUrl
        companyPhotos {
          caption
          url
        }
      }
      jobDetail {
        summary,
        jobDescription {
          html
        },
        jobRequirement {
          careerLevel
          yearsOfExperience
          qualification
          fieldOfStudy
          industryValue {
            value,
            label
          }
          skills
          employmentType
          languages
          postedDate
          closingDate
          jobFunctionValue {
            code,
            name,
            children {
              code,
              name
            }
          },
          benefits
        },
        whyJoinUs
      },
      location {
        location
        locationId
        omnitureLocationId
      }
      sourceCountry
    }
  }"}
