{"query":"
query VideoQuery($id: String!) {
  video(id: $id) {
    ...on Video {
      advertisingProperties {
        sensitivity
        sponsored
      }
      bylines {
        renderedRepresentation
      }
      contentSeries
      cues {
        name
        type
        timeIn
        timeOut
      }
      duration
      embedded
      headline {
        default
      }
      is360
      isLive
      liveUrls
      playlist {
        headline {
          default
        }
        promotionalHeadline
        url
        sourceId
        section {
          displayName
        }
        videos(first: 20) {
          edges @filterEmpty {
            node {
              advertisingProperties {
                sensitivity
                sponsored
              }
              id
              sourceId
              duration
              section {
                id
                name
              }
              headline {
                default
              }
              renditions {
                url
                type
              }
              url
              promotionalMedia {
                ... on Image {
                  crops(cropNames: [SMALL_SQUARE, MEDIUM_SQUARE, SIXTEEN_BY_NINE]) {
                    renditions {
                      name
                      width
                      height
                      url
                    }
                  }
                }
              }
            }
          }
        }
      }
      promotionalHeadline
      promotionalMedia {
        ... on Image {
          crops (cropNames: [SMALL_SQUARE, MEDIUM_SQUARE, SIXTEEN_BY_NINE, THREE_BY_TWO, TWO_BY_THREE, FLEXIBLE]) {
            name
            renditions {
              name
              width
              height
              url
            }
          }
        }
      }
      promotionalSummary
      related {
        ... on Article {
          promotionalHeadline
          url
          sourceId
        }
      }
      renditions {
        type
        width
        height
        url
        bitrate
      }
      section {
        name
      }
      shortUrl
      sourceId
      subsection {
        name
      }
      summary
      timesTags {
        __typename
        displayName
        isAdvertisingBrandSensitive
        vernacular
      }
      url
    }
  }
}","variables":{"id":"nyt://video/7314f11e-d0be-539c-9616-d37346d2030d"}}
