{"operationName":"tool","variables":{"id":"graphql"},"query":"query tool($id: ID!) {
  tool(id: $id) {
    id
    imageUrl
    thumbUrl
    thumbRetinaUrl
    name
    pressUrl
    contactFlow
    legacyThirdPartyId
    jobsCount
    alternativeTools(first: 5) {
      edges {
        node {
          id
          name
          description
          __typename
        }
        __typename
      }
      __typename
    }
    followers {
      count
      __typename
    }
    company {
      name
      amIOwner
      slug
      path
      stacks {
        id
        __typename
      }
      owners {
        amIAdmin
        canIModerate
        id
        __typename
      }
      __typename
    }
    pros(first: 15) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...reasonFields
          __typename
        }
        __typename
      }
      __typename
    }
    slug
    path
    verified
    title
    twitterUsername
    websiteUrl
    following
    githubStarsCount
    githubForksCount
    githubUpdatedAt
    hackernewsOnlineMentionsCount
    redditOnlineMentionsCount
    stackOverflowOnlineMentionsCount
    description
    ampStoryEnabled
    stacks
    votes
    jobs
    relatedStackups {
      count
      __typename
    }
    function {
      name
      slug
      __typename
    }
    layer {
      name
      slug
      __typename
    }
    category {
      name
      slug
      __typename
    }
    allToolIntegrations {
      count
      __typename
    }
    companyStacksUsing {
      count
      __typename
    }
    userStacksUsing {
      count
      __typename
    }
    features
    footerAlternateTools(first: 5) {
      edges {
        node {
          name
          path
          __typename
        }
        __typename
      }
      __typename
    }
    footerNewTools(first: 5) {
      edges {
        node {
          name
          path
          __typename
        }
        __typename
      }
      __typename
    }
    footerTopTools(first: 5) {
      edges {
        node {
          name
          path
          __typename
        }
        __typename
      }
      __typename
    }
    footerRelatedStackups(first: 5) {
      edges {
        node {
          path
          title
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment reasonFields on Reason {
  id
  upvoted
  upvotesCount
  text
  __typename
}
"}
