{"operationName":"stackupBySlug","variables":{"slug":"alfresco-vs-wordpress"},"query":"query stackupBySlug($slug: String!) {
  stackupBySlug(slug: $slug) {
    alternativeTools(first: 5) {
      edges {
        node {
          id
          name
          description
          __typename
        }
        __typename
      }
      count
      __typename
    }
    contactEnabledTools {
      id
      __typename
    }
    jobs {
      count
      __typename
    }
    applesToOranges
    content1
    content2
    topContent
    bottomContent
    screenshotImageUrl
    function
    path
    title
    seoUpdatedAt
    stackDecisions {
      ...stackupStackDecisionFields
      __typename
    }
    relatedStackups {
      ...relatedStackupFields
      __typename
    }
    descriptions(first: 100) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...descriptionFields
          __typename
        }
        __typename
      }
      __typename
    }
    reviews(first: 100) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...reviewFields
          __typename
        }
        __typename
      }
      __typename
    }
    footerRelatedStackups {
      title
      path
      __typename
    }
    footerTopStackups {
      title
      path
      __typename
    }
    footerTrendingStackups {
      title
      path
      __typename
    }
    __typename
  }
}

fragment reviewFields on Review {
  content
  createdAt
  tool {
    name
    imageUrl
    thumbUrl
    canonicalUrl
    __typename
  }
  user {
    id
    title
    companyName
    path
    username
    displayName
    imageUrl
    thumbUrl
    __typename
  }
  __typename
}

fragment descriptionFields on StackItem {
  tool {
    name
    canonicalUrl
    imageUrl
    thumbUrl
    __typename
  }
  description
  stack {
    owner {
      ... on Company {
        __typename
        name
        path
        imageUrl
        thumbUrl
      }
      ... on User {
        __typename
        displayName
        path
        imageUrl
        thumbUrl
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment stackupStackDecisionFields on StackDecision {
  id
  user {
    id
    username
    displayName
    title
    companyName
    imageUrl
    thumbUrl
    __typename
  }
  publicId
  publishedAt
  upvotesCount
  viewCount
  htmlContent
  services {
    name
    imageUrl
    thumbUrl
    __typename
  }
  topics {
    name
    __typename
  }
  company {
    name
    imageUrl
    thumbUrl
    path
    features {
      slug
      __typename
    }
    __typename
  }
  __typename
}

fragment relatedStackupFields on Stackup {
  id
  path
  services {
    id
    name
    imageUrl
    thumbUrl
    __typename
  }
  __typename
}
"}
