{"operationName":"stackDecisions","variables":{"id":"airbnb/airbnb"},"query":"query stackDecisions($id: String!) {
  stackProfile(identifier: $id) {
    id
    stackDecisions(first: 5) {
      count
      edges {
        node {
          ...stackDecisionFields
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment stackDecisionFields on StackDecision {
  id
  publicId
  htmlContent
  rawContent
  publishedAt
  commentsCount
  upvotesCount
  upvoted
  flagged
  bookmarked
  viewCount
  draft
  createdAt
  decisionType
  subjectTools {
    ...serviceFields
    __typename
  }
  fromTools {
    ...serviceFields
    __typename
  }
  toTools {
    ...serviceFields
    __typename
  }
  link {
    url
    title
    imageUrl
    __typename
  }
  company {
    id
    path
    imageUrl
    name
    slug
    __typename
  }
  topics {
    name
    __typename
  }
  services {
    ...serviceFields
    __typename
  }
  user {
    id
    username
    path
    imageUrl
    displayName
    title
    companyName
    __typename
  }
  rootComments {
    ...commentFields
    replies {
      ...commentFields
      parentId
      __typename
    }
    __typename
  }
  __typename
}

fragment commentFields on Comment {
  id
  content
  postedAt
  upvoted
  flagged
  upvotesCount
  parentId
  user {
    id
    path
    imageUrl
    displayName
    __typename
  }
  __typename
}

fragment serviceFields on Tool {
  id
  name
  slug
  title
  verified
  imageUrl
  canonicalUrl
  path
  votes
  fans
  stacks
  following
  followContext
  __typename
}
"}
